if float(image.width)/float(image.height) >= float(horizontal)/float(vertical):
    if image.height > int(height):
        image.transform(resize='%sx>' %int(float(height)*float(image.width)/float(image.height)))
        image.crop(width=int(float(height)/float(vertical)*float(horizontal)), height=int(height), gravity='center')
    else:
        image.crop(width=int(float(image.height)/float(vertical)*float(horizontal)), height=image.height, gravity='center')
        image.transform(resize='%sx>' %int(float(height)*float(image.width)/float(image.height)))
else:
    if float(image.width) > float(height)*float(horizontal)/float(vertical):
        image.transform(resize='%sx>' %int(float(height)*float(horizontal)/float(vertical)))
        image.crop(width=int(float(height)*float(horizontal)/float(vertical)), height=int(height), gravity='center')
    else:
        image.transform(resize='%sx>' %image.width)
        image.crop(width=image.width, height=int(float(image.width)/float(horizontal)*float(vertical)), gravity='center')
