if float(image.width)/image.height >= float(4)/3:
    image.transform(resize='%sx>' %int(float(image.width)/image.height*400))
else:
    image.transform(resize='%sx>' %int(float(4)/3*400))
image.crop(width=int(float(4)/3*400), height=400, gravity='center')

